package co.kr.myfitnote;

import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;

import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.LinearLayout;

import java.util.ArrayList;

import co.kr.myfitnote.core.ui.ButtonWithImageAndText;

/**
 * A simple {@link Fragment} subclass.
 * Use the {@link CompanyLinkFragment#newInstance} factory method to
 * create an instance of this fragment.
 */
public class CompanyLinkFragment extends Fragment {

    private ArrayList<DataItem> data = new ArrayList<>();

    // DataItem class representing each item in your data
    private class DataItem {
        String siteName;
        String href;
        String imageSrc;

        DataItem(String siteName, String href, String imageSrc) {
            this.siteName = siteName;
            this.href = href;
            this.imageSrc = imageSrc;
        }
    }


    public CompanyLinkFragment() {
        // Required empty public constructor
    }

    /**
     * Use this factory method to create a new instance of
     * this fragment using the provided parameters.
     *
     * @return A new instance of fragment CompanyLinkFragment.
     */
    // TODO: Rename and change types and number of parameters
    public static CompanyLinkFragment newInstance() {
        CompanyLinkFragment fragment = new CompanyLinkFragment();
        return fragment;
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view = inflater.inflate(R.layout.fragment_company_link, container, false);

        data.add(new DataItem("디캐릭", "https://www.dcarrick.co.kr/", "data:image/jpeg;base64,"));
        data.add(new DataItem("론픽", "http://www.ronfic.com/", "data:image/png;base64,"));
        data.add(new DataItem("딥다", "https://www.dipda.co.kr/", "https://www.dipda.co.kr/image/common/LOGO.png"));
        data.add(new DataItem("닥터다이어리", "http://www.drdiary.co.kr/main.html", "data:image/jpeg;base64,"));
        data.add(new DataItem("스포이즘", "http://spoizm.com/", "data:image/png;base64,"));
        data.add(new DataItem("제이어스", "http://www.jeios.com", "data:image/png;base64,"));

        // Find the container layout where you want to add buttons
        LinearLayout containerLayout = view.findViewById(R.id.companieslayout);

        // Iterate through data and add ButtonWithImageAndText dynamically
        for (DataItem item : data) {
            ButtonWithImageAndText button = new ButtonWithImageAndText(requireContext());
            button.setImage(item.imageSrc); // Set image
            button.setText(item.siteName); // Set text

            // Set OnClickListener to open the website
            button.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    openWebsite(item.href);
                }
            });

            // Add the button to the container layout
            containerLayout.addView(button);
        }

        return view;
    }

    private void openWebsite(String url) {
        // Open website
        Intent intent = new Intent(Intent.ACTION_VIEW, Uri.parse(url));
        startActivity(intent);
    }
}